import React, { useState } from 'react';
import { sendRequest } from '../utils/Api';
export const LoginScreen = () => {
    const [loginInput, setLoginInput] = useState({
        email: "",
        password: "",
    });
    const { email, password } = loginInput;
    const login = async () => {
        // console.log(email, password);
        const data  = await sendRequest(`login`, "POST", loginInput);
        console.log(data);
        // setPosts(data);
    };
    return (
        <div>
            <input placeholder="email" value={email} onChange={(e) => setLoginInput({ ...loginInput, email: e.target.value})}/>
            <input placeholder="password" value={password} onChange={(e) => setLoginInput({ ...loginInput, password: e.target.value})}/>
            <button onClick={login}>login</button>
        </div>
    )
}
